import { Component, Input, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators, FormBuilder } from '@angular/forms';
import { ServicioGeneralService } from 'src/app/services/servicio-general.service';
import { Datosdelcontrato } from '../../interfaces/datosdelcontrato';

@Component({
  selector: 'app-datosdelcontrato',
  templateUrl: './datosdelcontrato.component.html',
  styleUrls: ['./datosdelcontrato.component.css']
})
export class DatosdelcontratoComponent implements OnInit {

  frmDatoContrato!: FormGroup;
  submitted = false;

  // created
  constructor(
    private servicioGeneralService: ServicioGeneralService,
    private pFormBuilder: FormBuilder,
  ) {}

  // mounted 
  async ngOnInit() {
    this.setInitializeForm();
    this.onChanges();
  }

  //computed or Watch
  onChanges() {
    let inver = this.frmDatoContrato.get('inversionistainstitucional') as FormGroup;
    inver.valueChanges.subscribe(val => {
      this.servicioGeneralService.servicioGeneralObservableData = {
        inversionistaInstitucional: val
      }
    });
    this.frmDatoContrato.valueChanges.subscribe(val => {
      this.servicioGeneralService.servicioGeneralObservableData = {
        contratoIntermediacionCompleto : false
      }
    });

  }

  removeValidate(e: any){
    let porcentaje = this.frmDatoContrato.get('porcentaje') as FormGroup;
    let tipofirma = this.frmDatoContrato.get('tipofirma') as FormGroup;

    if(e.target.value == 'copropiedad'){
      porcentaje.setValidators(Validators.required);
      porcentaje.updateValueAndValidity();
      tipofirma.setValidators(Validators.required);
      tipofirma.updateValueAndValidity();
    } else {
      porcentaje.clearValidators();
      porcentaje.updateValueAndValidity();
      tipofirma.clearValidators();
      tipofirma.updateValueAndValidity();
      porcentaje.reset();
      tipofirma.reset();
    }
  }
  setInitializeForm() {
    this.InitializeFormDatoContrato();
  }
  InitializeFormDatoContrato(){
    this.frmDatoContrato = this.pFormBuilder.group({
      fecha: [null, Validators.required],
      tipopropiedad: [null, Validators.required],
      porcentaje: [null],
      tipofirma: [null],
      vinculado: [null, Validators.required],
      inversionistainstitucional: [null, Validators.required]
    })
  }
  onSubmit() {
    this.submitted = true
    if (this.frmDatoContrato.invalid) {
      return
    }
    this.servicioGeneralService.servicioGeneralObservableData = {
      contratoIntermediacionCompleto : true,
      tabmostrar: 2
    }
  }

}
