import { Component, OnInit } from '@angular/core';
import { Store } from '@ngrx/store';
import { Observable } from 'rxjs';
import { ServicioGeneralService } from 'src/app/services/servicio-general.service';

@Component({
  selector: 'app-tab',
  templateUrl: './tab.component.html',
  styleUrls: ['./tab.component.css']
})
export class TabComponent implements OnInit {
  public inversionistaInstitucional: Boolean = false
  constructor(private servicioGeneral: ServicioGeneralService) {
   }

  ngOnInit(): void {
    this.servicioGeneral.disparadorGeneral.subscribe(data => {
      console.log('recibiendodata', data)
      this.inversionistaInstitucional = data.inversionistaInstitucional
    })
  }

}
