import { Component, Input, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators, FormBuilder } from '@angular/forms';
import { ServicioGeneralService } from 'src/app/services/servicio-general.service';
import { Datosdelcontrato } from '../../interfaces/datosdelcontrato';

@Component({
  selector: 'app-datosdelcontrato',
  templateUrl: './datosdelcontrato.component.html',
  styleUrls: ['./datosdelcontrato.component.css']
})
export class DatosdelcontratoComponent implements OnInit {

  frmDatoContrato!: FormGroup;
  submitted = false;

  constructor(
    private servicioGeneralService: ServicioGeneralService,
    private pFormBuilder: FormBuilder,
  ) {}

  setInitializeForm() {
    this.InitializeFormDatoContrato();
  }
  InitializeFormDatoContrato(){
    this.frmDatoContrato = this.pFormBuilder.group({
      fecha: ['', Validators.required],
      tipoPropiedad: ['', Validators.required],
      porcentaje: ['', Validators.required],
      tipofirma: ['', Validators.required],
      vinculado: ['', Validators.required],
      inversionistainstitucional: ['', Validators.required]
    })
  }
  onSubmit() {
    if (this.frmDatoContrato.valid) {
      console.log("Form Submitted!");
    }
  }

  async ngOnInit() {
    this.setInitializeForm();
  }

}
